import React, { useState } from 'react';
import styled from "styled-components";

const Container = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    min-height: 100vh;
    padding: 20px;
    background-color: #D3E8F7;
    box-sizing: border-box;
`;

const Title = styled.div`
  font-size: 25px;
  font-family: "Pretendard-ExtraBold";
  color: #252A2F;
`;

const ImgPicker = styled.div`
  display: flex;
  position: relative;
`;

const ImgContainer = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  margin-top: 5%;
  gap: 5%;
`;

const RankBadge = styled.div`
  position: absolute;
  top: 10px;
  left: 10px;
  background-color: rgba(255, 255, 255, 0.7);
  color: black;
  border-radius: 50%;
  width: 25px;
  height: 25px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: bold;
`;

const UserSurvey = () => {
  const [ranks, setRanks] = useState(Array(5).fill(null)); // Î∞∞Ïó¥Î°ú ÏàúÏúÑÎ•º Ï†ÄÏû•

  const handleClick = (index) => {
    const nextRank = ranks.filter(rank => rank !== null).length + 1;
    const newRanks = ranks.slice();
    newRanks[index] = nextRank;
    setRanks(newRanks);
  };

  return (
    <Container>
      <Title>ÌôòÏòÅÌï¥Ïöî!üëãüèª</Title>
      <Title style={{marginTop:"5%"}}>Í∞ÄÍ≥† Ïã∂ÏùÄ ÏàúÏúºÎ°ú</Title>
      <Title>Ïó¨ÌñâÏßÄÎ•º Í≥®ÎùºÏ£ºÏÑ∏Ïöî</Title>
      {[...Array(5)].map((_, index) => (
        <ImgContainer key={index}>
          <ImgPicker onClick={() => handleClick(index)}>
            {ranks[index] && <RankBadge>{ranks[index]}</RankBadge>}
            <img 
              style={{width:"100%", borderRadius:"5%"}}
              src={process.env.PUBLIC_URL + `/asset/survey/img${index+1}.png`}
            />
          </ImgPicker>
        </ImgContainer>
      ))}
    </Container>
  );
};

export default UserSurvey;
