import React from 'react';
import styled from "styled-components";

const Container = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    min-height: 100vh;
    background-color: #D3E8F7;
    box-sizing: border-box;
    position: relative; /* 상대적 위치 설정 */
    z-index: 0; /* Container의 z-index 설정 */
`;

const Background = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: url(asset/background.jpg);
    mix-blend-mode: overlay;
    z-index: -1; /* 배경이 항상 다른 내용 뒤에 위치하도록 */
`;

const Title = styled.div`
    font-size: 30px;
    font-family: "Pretendard-ExtraBold";
    color: #252A2F;
    z-index: 1; /* 제목이 항상 가장 앞에 오도록 */
`;

const Message = styled.div`
    font-size: 10px;
    font-family: "Pretendard-Regular";
    color: #252A2F;
`;


const InitialPage = () => {
    const KAKAO_AUTH_URL = "/home" // 임시 경로, 이후 수정 필요

    return (
        <Container>
            <Background />
            <Message>당신을 위한 사운드 트립 큐레이션 서비스,</Message>
            <Title>TRIPBEATS</Title>
            <a href={KAKAO_AUTH_URL} className="kakaobtn">
                <img style={{width: "130px", boxShadow: "0 0 1px rgba(0,0,0,0.1)", borderRadius: "5px"}}
                src={process.env.PUBLIC_URL + `asset/kakao_login.png`} />
            </a>
            <Message sty>회원가입</Message>
            <Message>개인정보정책</Message>
        </Container>
    );
};

export default InitialPage;
