import React, { useState } from 'react';
import styled from "styled-components";

const Container = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    min-height: 100vh;
    padding: 20px;
    background-color: #D3E8F7;
    box-sizing: border-box;
`;

const Title = styled.div`
  font-size: 25px;
  font-family: "Pretendard-ExtraBold";
  color: #252A2F;
`;

const ImgPicker = styled.div`
  display: flex;
  position: relative;
`;

const ImgContainer = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  margin-top: 5%;
  gap: 5%;
`;

const RankBadge = styled.div`
  position: absolute;
  top: 10px;
  left: 10px;
  background-color: rgba(255, 255, 255, 0.7);
  color: black;
  border-radius: 50%;
  width: 25px;
  height: 25px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: bold;
`;

const UserSurvey = () => {
  const [ranks, setRanks] = useState(Array(5).fill(null));

  const handleClick = (index) => {
    const nextRank = ranks.filter(rank => rank !== null).length + 1;
    const newRanks = ranks.slice();
    newRanks[index] = nextRank;
    setRanks(newRanks);
  };

  return (
    <Container>
      <Title>환영해요!👋🏻</Title>
      <Title style={{marginTop:"5%"}}>가고 싶은 순으로</Title>
      <Title>여행지를 골라주세요</Title>
      <ImgContainer>
        <ImgPicker onClick={() => handleClick(0)}>
          <img 
            style={{width:"100%", borderRadius:"5%", position: "relative"}}
            src={process.env.PUBLIC_URL + `/asset/survey/img1.png`}
          />
          {ranks[0] && <RankBadge>{ranks[0]}</RankBadge>}
        </ImgPicker>
        <ImgPicker onClick={() => handleClick(1)}>
          <img 
            style={{width:"100%", borderRadius:"5%", position: "relative"}}
            src={process.env.PUBLIC_URL + `/asset/survey/img2.png`}
          />
          {ranks[1] && <RankBadge>{ranks[1]}</RankBadge>}
        </ImgPicker>
      </ImgContainer>
      <ImgContainer>
        <ImgPicker onClick={() => handleClick(2)}>
          <img 
            style={{width:"100%", borderRadius:"5%", position: "relative"}}
            src={process.env.PUBLIC_URL + `/asset/survey/img3.png`}
          />
          {ranks[2] && <RankBadge>{ranks[2]}</RankBadge>}
        </ImgPicker>
        <ImgPicker onClick={() => handleClick(3)}>
          <img 
            style={{width:"100%", borderRadius:"5%", position: "relative"}}
            src={process.env.PUBLIC_URL + `/asset/survey/img4.png`}
          />
          {ranks[3] && <RankBadge>{ranks[3]}</RankBadge>}
        </ImgPicker>
      </ImgContainer>
      <ImgContainer>
        <ImgPicker onClick={() => handleClick(4)}>
          <img 
            style={{width:"100%", borderRadius:"5%", position: "relative"}}
            src={process.env.PUBLIC_URL + `/asset/survey/img5.png`}
          />
          {ranks[4] && <RankBadge>{ranks[4]}</RankBadge>}
        </ImgPicker>
      </ImgContainer>
    </Container>
  );
};

export default UserSurvey;
