import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import Home from './component/page/Home';
import MyPage from './component/page/MyPage';
import Footer from './component/ui/Footer';
import styled from "styled-components";
import InitialPage from './component/page/InitialPage';
import LoginHandeler from './component/page/LoginHandeler';

const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  max-width: 500px;
  margin: 0 auto;
  background-color: #f8f8f8;
  min-height: 100vh;
  position: relative;
`;

const Content = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
`;

const FooterPlaceholder = styled.div`
  height: 50px; /* Footer의 높이와 동일하게 설정 */
`;

const App = () => {
  useEffect(() => {
    const setViewportHeight = () => {
      // window.innerHeight의 1%를 vh 단위로 계산하여, CSS 변수 --vh에 vh 값을 설정
      let vh = window.innerHeight * 0.01;
      document.documentElement.style.setProperty('--vh', `${vh}px`);
    };

    setViewportHeight();
    // 리사이즈 이벤트에 setViewportHeight 함수를 연결
    window.addEventListener('resize', setViewportHeight);

    // 클린업 함수: 컴포넌트가 언마운트될 때 리사이즈 이벤트 리스너를 제거
    return () => {
      window.removeEventListener('resize', setViewportHeight);
    };
  }, []);

  return (
    <Router>
      <AppContainer>
        <Content>
          <Routes>
            <Route path="/" element={<InitialPage />} />
            <Route path="/home" element={<Home />} />
            <Route path="/mypage" element={<MyPage />} />
            <Route
              path="/login/oauth2/callback/kakao"
              element={<LoginHandeler />}
            />
          </Routes>
        </Content>
        <FooterWithCondition />
      </AppContainer>
    </Router>
  );
};

const FooterWithCondition = () => {
  const location = useLocation();
  const noFooterPaths = ["/"]; // 여기에 Footer가 보이지 않을 경로를 추가하면 됨

  return !noFooterPaths.includes(location.pathname) ? <Footer /> : <FooterPlaceholder />;
};

export default App;
