import React, { useState } from 'react';
import styled from "styled-components";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  min-height: 100vh;
  padding: 20px;
  background-color: #D3E8F7;
  box-sizing: border-box;
`;

const Question = styled.div`
  font-size: 20px;
  font-family: "Pretendard-ExtraBold";
  color: #252A2F;
`;

const ButtonContainer = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  gap: 10%;
  margin-top: 5%;
`;

const Button = styled.button`
  background-color: ${props => (props.selected ? '#83a5c6' : 'white')};
  color: ${props => (props.selected ? 'white' : '#252A2F')};
  border: none;
  border-radius: 10px;
  width: 35%;
  height: 35px;
  font-size: 16px;
  font-family: "Pretendard-ExtraBold";
`;

const TasteSurvey = () => {
  const [selectedButtons, setSelectedButtons] = useState(Array(6).fill(false));

  const handleClick = (index) => {
    const newSelectedButtons = [...selectedButtons];
    newSelectedButtons[index] = !newSelectedButtons[index];
    setSelectedButtons(newSelectedButtons);
  };

  return (
    <Container>
      <Question>어떤 여행을 주로 다니셨나요?</Question>
      <ButtonContainer>
        <Button
          selected={selectedButtons[0]}
          onClick={() => handleClick(0)}
        >
          단거리
        </Button>
        <Button
          selected={selectedButtons[1]}
          onClick={() => handleClick(1)}
        >
          원거리
        </Button>
      </ButtonContainer>
      <ButtonContainer>
        <Button
          selected={selectedButtons[2]}
          onClick={() => handleClick(2)}
        >
          휴식
        </Button>
        <Button
          selected={selectedButtons[3]}
          onClick={() => handleClick(3)}
        >
          활동형
        </Button>
      </ButtonContainer>
      <ButtonContainer style={{marginBottom:"10%"}}>
        <Button
          selected={selectedButtons[4]}
          onClick={() => handleClick(4)}
        >
          자연
        </Button>
        <Button
          selected={selectedButtons[5]}
          onClick={() => handleClick(5)}
        >
          도심
        </Button>
      </ButtonContainer>
      <Question>그동안의 여행이나 취향과는 거리가 있는 새로운 여행지를 추천받고자 하는 의향을 얼마나 되시나요?</Question>
    </Container>
  );
};

export default TasteSurvey;
